$(document).ready(function(){
  $( ".options" ).change(function() {
   if($('#color').is(':checked')) {
      $( ".colorform" ).toggle();
     if ($(".speedform").is(":visible")) {
       $( ".speedform" ).toggle();
     }
     if ($(".modeform").is(":visible")) {
       $( ".modeform" ).toggle();
     }
   }
   else if ($('#speed').is(':checked')) { 
       $( ".speedform" ).toggle();
     if ($(".modeform").is(":visible")) {
       $( ".modeform" ).toggle();
     }
     if ($(".colorform").is(":visible")) {
       $( ".colorform" ).toggle();
     }
   }
   else if ($('#mode').is(':checked')) { 
       $( ".modeform" ).toggle();
     if ($(".speedform").is(":visible")) {
       $( ".speedform" ).toggle();
     }
     if ($(".colorform").is(":visible")) {
       $( ".colorform" ).toggle();
     }
   }
  });
  $( ".advancedmode" ).change(function() {
    if($('#advancedmode').is(':checked')) {
      $( ".advanced" ).show();
    }
    else {
      $( ".advanced" ).hide();
    }
  });
});